mapper.select:
select 

from ad_showcase_busi_sale
where eff_date = #{effDate,jdbcType=TIMESTAMP}
  and showbit_id = #{showbitId,jdbcType=INTEGER}

mapper.delete:
delete from ad_showcase_busi_sale
where eff_date = #{effDate,jdbcType=TIMESTAMP}
  and showbit_id = #{showbitId,jdbcType=INTEGER}

mapper.update:
update ad_showcase_busi_sale
set exp_date = #{expDate,jdbcType=TIMESTAMP},
  sale_shop_id = #{saleShopId,jdbcType=BIGINT},
  sale_price = #{salePrice,jdbcType=DOUBLE},
  sale_date = #{saleDate,jdbcType=TIMESTAMP},
  pay_statu = #{payStatu,jdbcType=CHAR}
where eff_date = #{effDate,jdbcType=TIMESTAMP}
  and showbit_id = #{showbitId,jdbcType=INTEGER}
