mapper.select:
select 

from ad_showpage_dict
where eff_date = #{effDate,jdbcType=TIMESTAMP}
  and page_id = #{pageId,jdbcType=BIGINT}
  and shop_id = #{shopId,jdbcType=BIGINT}
  and show_id = #{showId,jdbcType=BIGINT}

SELECT d.shop_name, c.show_name, a.page_id, a.site_count, a.eff_date , a.exp_date , a.page_no FROM ad_showpage_dict a, ad_showcase_dict c, gd_shop_info d WHERE a.shop_id = d.shop_id AND a.show_id = c.show_id
and a.show_id = #{showId,jdbcType=BIGINT}
and a.site_count = #{siteCount,jdbcType=INTEGER}
and a.page_no = #{pageNo,jdbcType=INTEGER}
ORDER BY a.page_id

mapper.delete:
delete from ad_showpage_dict
where eff_date = #{effDate,jdbcType=TIMESTAMP}
  and page_id = #{pageId,jdbcType=BIGINT}
  and shop_id = #{shopId,jdbcType=BIGINT}
  and show_id = #{showId,jdbcType=BIGINT}

mapper.update:
update ad_showpage_dict


    show_template_id = #{showTemplateId,jdbcType=INTEGER},


    exp_date = #{expDate,jdbcType=TIMESTAMP},


    site_count = #{siteCount,jdbcType=INTEGER},


    state = #{state,jdbcType=CHAR},


where eff_date = #{effDate,jdbcType=TIMESTAMP}
  and page_id = #{pageId,jdbcType=BIGINT}
  and shop_id = #{shopId,jdbcType=BIGINT}
  and show_id = #{showId,jdbcType=BIGINT}

update ad_showpage_dict
set show_template_id = #{showTemplateId,jdbcType=INTEGER},
  exp_date = #{expDate,jdbcType=TIMESTAMP},
  site_count = #{siteCount,jdbcType=INTEGER},
  state = #{state,jdbcType=CHAR}
where eff_date = #{effDate,jdbcType=TIMESTAMP}
  and page_id = #{pageId,jdbcType=BIGINT}
  and shop_id = #{shopId,jdbcType=BIGINT}
  and show_id = #{showId,jdbcType=BIGINT}
