mapper.update:
update t_itov_integral_total
set integral =integral+ #{integral,jdbcType=INTEGER}
where account_id = #{account_id,jdbcType=BIGINT}

mapper.select:
select account_id,rule_type,integral
from t_itov_integral_total a
where a.account_id = #{account_id,jdbcType=BIGINT}

select id,type,integral,name
from t_itov_integral_rule a
where a.type = #{type,jdbcType=INTEGER}

	select account_id,crt_date from t_itov_account where account_type=1 

     and crt_date > #{crt_date,jdbcType=TIMESTAMP}

    order by crt_date asc

SELECT a.account_id, calldatetime crt_date FROM `t_itov_ptt_call_log` c INNER JOIN `t_itov_personal` p ON c.callfrom=p.personal_tel INNER JOIN `t_itov_account` a ON a.account_id=p.account_id WHERE callduration >0 AND (errorcode='0' OR errorcode='104026') AND agentid IS NOT NULL AND a.account_type=1
AND calldatetime > #{crt_date,jdbcType=TIMESTAMP}
ORDER BY calldatetime ASC

SELECT s.`account_id`, g.crtdate crt_date FROM `t_itov_ptt_subaccount` s INNER JOIN `t_itov_ptt_subaccount_log` g ON s.`voipAccount`=g.`caller` WHERE (byetype=1 OR byetype=2)
AND g.crtdate > #{crt_date,jdbcType=TIMESTAMP}
ORDER BY crtdate ASC
