mapper.select:
select max(order_id)
from gd_shop_logistics
where shop_id = #{shopId,jdbcType=BIGINT}

select a.order_id, a.shop_id, a.province_code, a.area_code, a.city_code, a.send_end_time, a.send_start_time, a.user_name, a.mobile, a.contact_phone, a.address, a.post_code, b.city_name province_name, c.city_name city_name, d.city_name area_name from gd_shop_logistics a, sy_city_dict b, sy_city_dict c, sy_city_dict d
a.province_code = b.city_code and a.city_code = c.city_code and a.area_code = d.city_code
AND shop_id = #{shopId,jdbcType=BIGINT}
and order_id = #{orderId,jdbcType=INTEGER}
and city_code = #{cityCode,jdbcType=INTEGER}
order by order_id

select 
count(1)
from gd_shop_logistics a, sy_city_dict b, sy_city_dict c, sy_city_dict d

   a.province_code = b.city_code
   and a.city_code = c.city_code
   and a.area_code = d.city_code

        AND shop_id = #{shopId,jdbcType=BIGINT}


        and order_id = #{orderId,jdbcType=INTEGER}


        and city_code = #{cityCode,jdbcType=INTEGER}

select 

from gd_shop_logistics
where order_id = #{orderId,jdbcType=INTEGER}
  and shop_id = #{shopId,jdbcType=BIGINT}

mapper.delete:
delete from gd_shop_logistics
where order_id = #{orderId,jdbcType=INTEGER}
  and shop_id = #{shopId,jdbcType=BIGINT}

mapper.insert:
insert into gd_shop_logistics (order_id, shop_id, province_code, 
