  send_start_time, user_name, mobile, 
  contact_phone, address, post_code
  )
values (#{orderId,jdbcType=INTEGER}, #{shopId,jdbcType=BIGINT}, #{provinceCode,jdbcType=INTEGER}, 
  #{areaCode,jdbcType=INTEGER}, #{cityCode,jdbcType=INTEGER}, #{sendEndTime,jdbcType=TIMESTAMP}, 
  #{sendStartTime,jdbcType=TIMESTAMP}, #{userName,jdbcType=VARCHAR}, #{mobile,jdbcType=VARCHAR}, 
  #{contactPhone,jdbcType=VARCHAR}, #{address,jdbcType=VARCHAR}, #{postCode,jdbcType=CHAR}
  )
