        AND client_type = #{clientType,jdbcType=CHAR}

select 
count(1)
from sy_client_ver


        AND client_id = #{clientId,jdbcType=INTEGER}


        AND company_id = #{companyId,jdbcType=INTEGER}


        AND client_name like CONCAT('%',#{clientName,jdbcType=VARCHAR},'%')  


        AND client_type = #{clientType,jdbcType=CHAR}

