mapper.select:
select 

from sy_hot_words


        AND hotwords_id = #{hotwordsId,jdbcType=INTEGER}


        AND hotwords_name like CONCAT('%',#{hotwordsName,jdbcType=VARCHAR},'%')  


        AND http_url = #{httpUrl,jdbcType=VARCHAR}  


        AND acount >= #{acountMin} 


        AND acount <= #{acountMax}

select 
count(1)
from sy_hot_words


        AND hotwords_id = #{hotwordsId,jdbcType=INTEGER}


        AND hotwords_name like CONCAT('%',#{hotwordsName,jdbcType=VARCHAR},'%')  


        AND http_url = #{httpUrl,jdbcType=VARCHAR}  


        AND acount >= #{acountMin} 


        AND acount <= #{acountMax}

mapper.update:
update sy_hot_words
set hotwords_name = #{hotwordsName,jdbcType=VARCHAR},
  acount = #{acount,jdbcType=INTEGER},
  http_url = #{httpUrl,jdbcType=VARCHAR}
where hotwords_id = #{hotwordsId,jdbcType=INTEGER}
