mapper.select:
select 

from us_buy_items
where items_id = #{itemsId,jdbcType=BIGINT}
  and shop_id = #{shopId,jdbcType=BIGINT}
  and user_id = #{userId,jdbcType=BIGINT}

select 
	count(1)
from us_buy_items a,gd_items_info b
where a.shop_id = b.shop_id and a.items_id  = b.items_id      

  and a.items_id = #{itemsId,jdbcType=BIGINT}


  and a.shop_id = #{shopId,jdbcType=BIGINT}


  and a.user_id = #{userId,jdbcType=BIGINT}

select 
	a.user_id,a.shop_id,a.items_id, b.small_pic ,a.discount,a.create_time,b.items_name
from us_buy_items a,gd_items_info b
where a.shop_id = b.shop_id and a.items_id  = b.items_id      

  and a.items_id = #{itemsId,jdbcType=BIGINT}


  and a.shop_id = #{shopId,jdbcType=BIGINT}


  and a.user_id = #{userId,jdbcType=BIGINT}

  order by a.create_time desc

    LIMIT #{startLimit,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

select 
	distinct b.shop_id,b.shop_name ,b.scan_pic
from us_buy_items a, gd_shop_info b
where a.shop_id = b.shop_id      

  and a.items_id = #{itemsId,jdbcType=BIGINT}


  and a.shop_id = #{shopId,jdbcType=BIGINT}


  and a.user_id = #{userId,jdbcType=BIGINT}

 order by a.create_time desc

mapper.delete:
delete from us_buy_items
where items_id = #{itemsId,jdbcType=BIGINT}
  and shop_id = #{shopId,jdbcType=BIGINT}
  and user_id = #{userId,jdbcType=BIGINT}

mapper.update:
update us_buy_items


    discount = #{discount,jdbcType=DOUBLE},


    create_time = #{createTime,jdbcType=TIMESTAMP},


where items_id = #{itemsId,jdbcType=BIGINT}
  and shop_id = #{shopId,jdbcType=BIGINT}
  and user_id = #{userId,jdbcType=BIGINT}

update us_buy_items
set discount = #{discount,jdbcType=DOUBLE},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where items_id = #{itemsId,jdbcType=BIGINT}
  and shop_id = #{shopId,jdbcType=BIGINT}
  and user_id = #{userId,jdbcType=BIGINT}
