mapper.select:
select 

from us_invoice_cfg
where orders = #{orders,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=BIGINT}

select ifnull(max(orders),0)+1 as orders 
from us_invoice_cfg
where user_id = #{userId,jdbcType=BIGINT}

mapper.delete:
delete from us_invoice_cfg
where orders = #{orders,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=BIGINT}

mapper.update:
update us_invoice_cfg
set invoice_title = #{invoiceTitle,jdbcType=VARCHAR},
  invoice_content = #{invoiceContent,jdbcType=VARCHAR},
  identification = #{identification,jdbcType=VARCHAR},
  mark = #{mark,jdbcType=VARCHAR},
  op_time = #{opTime,jdbcType=TIMESTAMP}
where orders = #{orders,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=BIGINT}
