mapper.select:
select a.items_id, a.shop_id, a.user_id, b.discount, a.create_time, a.acount, b.show_type, b.data_type, b.newtype_type, b.item_type, b.has_child, b.items_name, b.name_spr, b.items_intro, b.small_pic, b.big_pic, b.http_url, b.web_url, b.items_count, b.price, b.aunit, b.state,b.member_price, b.sale_count, b.discuss30_count, b.discuss_count, b.collect_cout, b.bar_code, b.serch_up, b.credit_percent, b.credit, b.deliver_flag, b.out_code, b.product_id, b.order_value, b.invoice_flag, b.back_order_flag, b.update_time, b.update_no, b.city_code, b.province_code, b.comment_value, b.items_weight, c.shop_name, c.web_url as shop_web_url, c.http_url as shop_http_url from us_shop_cart a, gd_items_info b, gd_shop_info c where a.items_id = b.items_id and a.shop_id = c.shop_id
and a.items_id = #{itemsId,jdbcType=BIGINT}
and a.shop_id = #{shopId,jdbcType=BIGINT}
and a.user_id = #{userId,jdbcType=BIGINT}
order by a.shop_id

select count(1)
  from us_shop_cart
where user_id = #{userId,jdbcType=BIGINT}

  and a.shop_id = #{shopId,jdbcType=BIGINT}

select 

from us_shop_cart
where items_id = #{itemsId,jdbcType=BIGINT}
  and shop_id = #{shopId,jdbcType=BIGINT}
  and user_id = #{userId,jdbcType=BIGINT}

mapper.delete:
delete from us_shop_cart
where items_id = #{itemsId,jdbcType=BIGINT}
  and shop_id = #{shopId,jdbcType=BIGINT}
  and user_id = #{userId,jdbcType=BIGINT}

mapper.insert:
insert into us_shop_cart (items_id, shop_id, user_id, 
  discount, create_time, acount
  )
values (#{itemsId,jdbcType=BIGINT}, #{shopId,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}, 
  #{discount,jdbcType=DOUBLE}, #{createTime,jdbcType=TIMESTAMP}, #{acount,jdbcType=INTEGER}
  )

mapper.update:
update us_shop_cart


    discount = #{discount,jdbcType=DOUBLE},


    create_time = #{createTime,jdbcType=TIMESTAMP},


    acount = #{acount,jdbcType=INTEGER},


where items_id = #{itemsId,jdbcType=BIGINT}
  and shop_id = #{shopId,jdbcType=BIGINT}
  and user_id = #{userId,jdbcType=BIGINT}

update us_shop_cart
set discount = #{discount,jdbcType=DOUBLE},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  acount = #{acount,jdbcType=INTEGER}
where items_id = #{itemsId,jdbcType=BIGINT}
  and shop_id = #{shopId,jdbcType=BIGINT}
  and user_id = #{userId,jdbcType=BIGINT}
