mapper.update:
update us_user_address
   set post_addr = '0'
where user_id = #{userId,jdbcType=BIGINT}

mapper.select:
select a.orders, a.user_id, a.post_addr, a.province_code, a.city_code, a.area_code, a.address, a.post_code, a.content_phone, a.mobile, a.user_name, a.arrival_time, a.create_time, a.update_time, b.city_name as province_name, c.city_name, d.city_name as area_name, e.arrival_name from us_user_address a, sy_city_dict b, sy_city_dict c, sy_city_dict d, re_arrival_time_dic e where a.province_code = b.city_code and a.city_code = c.city_code and a.area_code = d.city_code and a.arrival_time = e.arrival_time
and a.user_id = #{userId,jdbcType=BIGINT}
and a.update_time > #{updateTime,jdbcType=BIGINT}
order by a.orders

select max(orders)
from us_user_address
where user_id = #{userId,jdbcType=BIGINT}

select 

from us_user_address
where orders = #{orders,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=BIGINT}

mapper.delete:
delete from us_user_address
where orders = #{orders,jdbcType=INTEGER}
  and user_id = #{userId,jdbcType=BIGINT}

mapper.select:
select ifnull(max(orders),0)+1 as order_num 
from us_user_address
where user_id = #{userId,jdbcType=BIGINT}
