mapper.select:
SELECT 

FROM
ad_showbusi_acc
WHERE showarea_id = #{showareaId,jdbcType=INTEGER}
AND show_id = #{showId,jdbcType=BIGINT}
AND show_page_no = #{showPageNo,jdbcType=INTEGER}
AND shop_id = #{shopId,jdbcType=BIGINT}
AND NOW()BETWEEN showsite_eff_date
AND showsite_exp_date
AND is_default = #{isDefault,jdbcType=CHAR}
AND showsite_valid = 'Y'
ORDER BY show_page_no
