mapper.insert:
insert into ad_showcase_dict (show_id, showarea_id, show_name, 
  show_parent_id, show_level, is_leaf, 
  items_type, order_by, child_layout, 
  show_width, show_high, page_count
  )
values (nextval('show_id'), #{showareaId,jdbcType=INTEGER}, #{showName,jdbcType=VARCHAR}, 
  #{showParentId,jdbcType=BIGINT}, #{showLevel,jdbcType=INTEGER}, #{isLeaf,jdbcType=CHAR}, 
  #{itemsType,jdbcType=INTEGER}, #{orderBy,jdbcType=INTEGER}, #{childLayout,jdbcType=INTEGER}, 
  #{showWidth,jdbcType=DOUBLE}, #{showHigh,jdbcType=DOUBLE}, #{pageCount,jdbcType=INTEGER}
  )

mapper.select:
SELECT a.show_id, a.show_name, b.type_name, c.showarea_name FROM ad_showcase_dict a, gd_items_type_dic b, ad_showarea_dict c WHERE a.items_type = b.items_type AND a.showarea_id = c.showarea_id AND b.data_type = '10' AND b.parent_id = '-1'
and c.showarea_name like CONCAT('%',#{showareaName,jdbcType=VARCHAR},'%')
and a.show_name like CONCAT('%',#{showName,jdbcType=VARCHAR},'%')
and a.items_type = #{itemsType,jdbcType=INTEGER}
and b.type_name like CONCAT('%',#{typeName,jdbcType=VARCHAR},'%')
limit #{start},#{offset}

SELECT count(1) FROM ad_showcase_dict a, gd_items_type_dic b, ad_showarea_dict c WHERE a.items_type = b.items_type AND a.showarea_id = c.showarea_id AND b.data_type = '10' AND b.parent_id = '-1'
and c.showarea_name like CONCAT('%',#{showareaName,jdbcType=VARCHAR},'%')
and a.show_name like CONCAT('%',#{showName,jdbcType=VARCHAR},'%')
and a.items_type = #{itemsType,jdbcType=INTEGER}
and b.type_name like CONCAT('%',#{typeName,jdbcType=VARCHAR},'%')
limit #{start},#{offset}

SELECT a.showarea_id, a.showarea_name, b.show_id, b.show_name, b.show_width, b.show_high, b.page_count, (SELECT COUNT(*) FROM ad_showpage_dict c WHERE c.show_id = b.show_id) AS flag FROM ad_showarea_dict a, ad_showcase_dict b WHERE a.showarea_id = b.showarea_id
and a.showarea_id = #{showareaId,jdbcType=INTEGER}
and b.show_id = #{showId,jdbcType=INTEGER}
and b.page_count = #{pageCount,jdbcType=INTEGER}
AND (SELECT COUNT(*) FROM ad_showpage_dict c WHERE c.show_id = b.show_id) = #{flag,jdbcType=INTEGER}
limit #{start},#{offset}

  	SELECT 
		count(1)
	FROM 
		ad_showarea_dict a,
		ad_showcase_dict b 
	WHERE a.showarea_id = b.showarea_id


			and a.showarea_id = #{showareaId,jdbcType=INTEGER}


			and b.show_id = #{showId,jdbcType=INTEGER}


			and b.page_count = #{pageCount,jdbcType=INTEGER}


			AND (SELECT COUNT(*) FROM ad_showpage_dict c WHERE c.show_id = b.show_id) = #{flag,jdbcType=INTEGER}


			limit #{start},#{offset}
