mapper.select:
select 

from gd_items_info a, ac_action_items b
where a.items_id = b.items_id
  and b.actions_id = #{actionsId,jdbcType=BIGINT}
  and a.items_id = #{itemsId,jdbcType=BIGINT}

select 

from gd_items_info a, ac_action_items b
where a.items_id = b.items_id
  and b.actions_id = #{actionsId,jdbcType=BIGINT}

  and b.orders_time < #{ordersTime,jdbcType=TIMESTAMP}

order by b.orders_time desc

  limit #{fetchRows,jdbcType=INTEGER}

select 
count(1)
from gd_items_info a, ac_action_items b
where a.items_id = b.items_id
  and b.actions_id = #{actionsId,jdbcType=BIGINT}

  and b.orders_time < #{ordersTime,jdbcType=TIMESTAMP}

select 

from ac_action_items
where actions_id = #{actionsId,jdbcType=BIGINT}
  and items_id = #{itemsId,jdbcType=BIGINT}

mapper.delete:
delete from ac_action_items
where actions_id = #{actionsId,jdbcType=BIGINT}
  and items_id = #{itemsId,jdbcType=BIGINT}

mapper.update:
update ac_action_items
set data_type = #{dataType,jdbcType=INTEGER},
  orders_time = #{ordersTime,jdbcType=TIMESTAMP},
  orders = #{orders,jdbcType=INTEGER},
  update_time = #{updateTime,jdbcType=TIMESTAMP},
  state = #{state,jdbcType=CHAR},
  update_no = #{updateNo,jdbcType=INTEGER}
where actions_id = #{actionsId,jdbcType=BIGINT}
  and items_id = #{itemsId,jdbcType=BIGINT}
