mapper.select:
select 

from re_comment_log
where items_id = #{itemsId,jdbcType=BIGINT}
  and order_no = #{orderNo,jdbcType=BIGINT}

mapper.delete:
delete from re_comment_log
where items_id = #{itemsId,jdbcType=BIGINT}
  and order_no = #{orderNo,jdbcType=BIGINT}

mapper.update:
update re_comment_log


    user_id = #{userId,jdbcType=BIGINT},


    shop_id = #{shopId,jdbcType=BIGINT},


where items_id = #{itemsId,jdbcType=BIGINT}
  and order_no = #{orderNo,jdbcType=BIGINT}

update re_comment_log
set user_id = #{userId,jdbcType=BIGINT},
  shop_id = #{shopId,jdbcType=BIGINT}
where items_id = #{itemsId,jdbcType=BIGINT}
  and order_no = #{orderNo,jdbcType=BIGINT}
