mapper.select:
select 

from re_order_state_rel


   function_code = #{functionCode,jdbcType=VARCHAR}


   and order_condition = #{orderCondition,jdbcType=INTEGER}


    and order_state = #{orderState,jdbcType=INTEGER}


    and next_order_state = #{nextOrderState,jdbcType=INTEGER}

select 

from re_order_state_rel
where function_code = #{functionCode,jdbcType=VARCHAR}
  and order_condition = #{orderCondition,jdbcType=INTEGER}
  and order_state = #{orderState,jdbcType=INTEGER}

mapper.delete:
delete from re_order_state_rel
where function_code = #{functionCode,jdbcType=VARCHAR}
  and order_condition = #{orderCondition,jdbcType=INTEGER}
  and order_state = #{orderState,jdbcType=INTEGER}

mapper.update:
update re_order_state_rel


    next_order_state = #{nextOrderState,jdbcType=INTEGER},


    mark = #{mark,jdbcType=VARCHAR},


    update_time = #{updateTime,jdbcType=TIMESTAMP},


    update_no = #{updateNo,jdbcType=INTEGER},


where function_code = #{functionCode,jdbcType=VARCHAR}
  and order_condition = #{orderCondition,jdbcType=INTEGER}
  and order_state = #{orderState,jdbcType=INTEGER}

update re_order_state_rel
set next_order_state = #{nextOrderState,jdbcType=INTEGER},
  mark = #{mark,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP},
  update_no = #{updateNo,jdbcType=INTEGER}
where function_code = #{functionCode,jdbcType=VARCHAR}
  and order_condition = #{orderCondition,jdbcType=INTEGER}
  and order_state = #{orderState,jdbcType=INTEGER}
