mapper.select:
select b.user_id, d.user_name, d.user_account, b.create_date, b.state, c.state_name, sum(a.all_price) as all_price, sum(a.acount) as acount from re_user_order_detail a, re_user_order b, re_state_dic c, us_user_info d where a.order_no= b.order_no and b.state = c.order_state and b.user_id = d.user_id and a.items_id = #{itemsId,jdbcType=BIGINT} and DATE_FORMAT(b.create_date, '%Y%m') = DATE_FORMAT(CURDATE(), '%Y%m')
and b.state = #{state,jdbcType=CHAR}
group by b.user_id, d.user_name, d.user_account, b.create_date, b.state, c.state_name limit #{start},#{end}

mapper.update:
update re_user_order_detail
set op_time = #{opTime,jdbcType=TIMESTAMP},
    state = #{state,jdbcType=INTEGER}
where order_no = #{orderNo,jdbcType=BIGINT}

mapper.select:
select 

from re_user_order_detail
where items_id = #{itemsId,jdbcType=BIGINT}
  and order_no = #{orderNo,jdbcType=BIGINT}

mapper.delete:
delete from re_user_order_detail
where items_id = #{itemsId,jdbcType=BIGINT}
  and order_no = #{orderNo,jdbcType=BIGINT}
