mapper.select:
select 

from gd_items_type_flat
where child_items = #{childItems,jdbcType=INTEGER}
  and items_type = #{itemsType,jdbcType=INTEGER}

mapper.delete:
delete from gd_items_type_flat
where child_items = #{childItems,jdbcType=INTEGER}
  and items_type = #{itemsType,jdbcType=INTEGER}

mapper.insert:
insert into gd_items_type_flat (child_items, items_type, level_no
  )
values (#{childItems,jdbcType=INTEGER}, #{itemsType,jdbcType=INTEGER}, #{levelNo,jdbcType=INTEGER}
  )

mapper.update:
update gd_items_type_flat


    level_no = #{levelNo,jdbcType=INTEGER},


where child_items = #{childItems,jdbcType=INTEGER}
  and items_type = #{itemsType,jdbcType=INTEGER}

update gd_items_type_flat
set level_no = #{levelNo,jdbcType=INTEGER}
where child_items = #{childItems,jdbcType=INTEGER}
  and items_type = #{itemsType,jdbcType=INTEGER}
