mapper.select:
  	  SELECT a.res_uri
		FROM gd_resouce_dic a,
		  gd_pic_res_rel b,
		  sy_resource_size_dic c
		WHERE b.table_name = #{tableName,jdbcType=VARCHAR}
		    AND b.record_id = #{recordId,jdbcType=INTEGER}
		    AND b.res_id = a.res_id
		    AND b.store_type = c.store_type
		    AND b.pic_size_type = c.pic_size_type
		    AND b.store_type = #{storeType,jdbcType=INTEGER}
