mapper.select:
select max(deliver_id)
from gd_shop_deliver
where shop_id = #{shopId,jdbcType=BIGINT}

select 

from gd_shop_deliver
where deliver_id = #{deliverId,jdbcType=BIGINT}
  and shop_id = #{shopId,jdbcType=BIGINT}

mapper.delete:
delete from gd_shop_deliver
where deliver_id = #{deliverId,jdbcType=BIGINT}
  and shop_id = #{shopId,jdbcType=BIGINT}

mapper.update:
update gd_shop_deliver


    deliver_mode_name = #{deliverModeName,jdbcType=VARCHAR},


    update_time = #{updateTime,jdbcType=TIMESTAMP},


where deliver_id = #{deliverId,jdbcType=BIGINT}
  and shop_id = #{shopId,jdbcType=BIGINT}

update gd_shop_deliver
set deliver_mode_name = #{deliverModeName,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where deliver_id = #{deliverId,jdbcType=BIGINT}
  and shop_id = #{shopId,jdbcType=BIGINT}
