mapper.select:
select 

from sy_data_type_dic
where data_type = #{dataType,jdbcType=INTEGER}
  and use_type = #{useType,jdbcType=INTEGER}

mapper.delete:
delete from sy_data_type_dic
where data_type = #{dataType,jdbcType=INTEGER}
  and use_type = #{useType,jdbcType=INTEGER}

mapper.insert:
insert into sy_data_type_dic (data_type, use_type, orders, 
  data_name, state, data_mark
  )
values (#{dataType,jdbcType=INTEGER}, #{useType,jdbcType=INTEGER}, #{orders,jdbcType=INTEGER}, 
  #{dataName,jdbcType=VARCHAR}, #{state,jdbcType=CHAR}, #{dataMark,jdbcType=VARCHAR}
  )

mapper.update:
update sy_data_type_dic
set orders = #{orders,jdbcType=INTEGER},
  data_name = #{dataName,jdbcType=VARCHAR},
  state = #{state,jdbcType=CHAR},
  data_mark = #{dataMark,jdbcType=VARCHAR}
where data_type = #{dataType,jdbcType=INTEGER}
  and use_type = #{useType,jdbcType=INTEGER}
