mapper.select:
select 

from sy_normal_dics
where column_name = #{columnName,jdbcType=VARCHAR}
  and table_name = #{tableName,jdbcType=VARCHAR}

select 

from sy_normal_dics
where column_name = #{columnName,jdbcType=VARCHAR}
  and dic_value = #{dicValue,jdbcType=INTEGER}
  and table_name = #{tableName,jdbcType=VARCHAR}

mapper.delete:
delete from sy_normal_dics
where column_name = #{columnName,jdbcType=VARCHAR}
  and dic_value = #{dicValue,jdbcType=INTEGER}
  and table_name = #{tableName,jdbcType=VARCHAR}

mapper.update:
update sy_normal_dics


    dic_name = #{dicName,jdbcType=VARCHAR},


    dic_mark = #{dicMark,jdbcType=VARCHAR},


    dic_state = #{dicState,jdbcType=CHAR},


    create_no = #{createNo,jdbcType=INTEGER},


    create_date = #{createDate,jdbcType=TIMESTAMP},


where column_name = #{columnName,jdbcType=VARCHAR}
  and dic_value = #{dicValue,jdbcType=INTEGER}
  and table_name = #{tableName,jdbcType=VARCHAR}

update sy_normal_dics
set dic_name = #{dicName,jdbcType=VARCHAR},
  dic_mark = #{dicMark,jdbcType=VARCHAR},
  dic_state = #{dicState,jdbcType=CHAR},
  create_no = #{createNo,jdbcType=INTEGER},
  create_date = #{createDate,jdbcType=TIMESTAMP}
where column_name = #{columnName,jdbcType=VARCHAR}
  and dic_value = #{dicValue,jdbcType=INTEGER}
  and table_name = #{tableName,jdbcType=VARCHAR}
