mapper.select:
select 

from sy_serv_dic a
where a.servtype_id = #{servtypeId,jdbcType=INTEGER}

mapper.insert:
insert into sy_serv_dic (servtype_id, servtype_name, servtype_desc
  )
values (#{servtypeId,jdbcType=INTEGER}, #{servtypeName,jdbcType=VARCHAR}, #{servtypeDesc,jdbcType=VARCHAR}
  )

mapper.update:
update sy_serv_dic
set servtype_name = #{servtypeName,jdbcType=VARCHAR},
  servtype_desc = #{servtypeDesc,jdbcType=VARCHAR}
where servtype_id = #{servtypeId,jdbcType=INTEGER}

mapper.select:
select 

from sy_serv_dic a,gd_shop_serv_rel b
where a.servtype_id = b.servtype_id and b.shop_id = #{shopId,jdbcType=BIGINT}

select count(*)
from sy_serv_dic a,gd_shop_serv_rel b
where a.servtype_id = b.servtype_id and b.shop_id = #{shopId,jdbcType=BIGINT}
