mapper.update:
update
t_itov_ptt_subaccount

set agentstate = #{agentstate,jdbcType=VARCHAR}

where voipaccount = #{voipaccount,jdbcType=VARCHAR}

mapper.select:
SELECT b.`account_name` AS terminal_imei FROM t_itov_account b INNER JOIN
t_zdc_terminal_account a ON b.`account_id`=a.account_id_ter
WHERE a.account_id_tel=#{account_id,jdbcType=VARCHAR}

SELECT subacc.`voipAccount`,sublog.`called`,sublog.`caller`,sublog.`endtime`,sublog.`starttime`,sublog.`recordurl`,sublog.`byetype`,sublog.`type`,subacc.`account_id`,subacc.`voip_status`,hostuser.`name`,hostuser.`telphone`,hostuser.`sex`,hostuser.`email` FROM   `t_itov_ptt_subaccount_log` sublog,`t_itov_ptt_subaccount` subacc ,`t_itov_account` account,`t_zdc_host_user` hostuser WHERE sublog.`called`=subacc.`voipAccount`
 AND subacc.`account_id` = account.`account_id`AND subacc.`account_id` = hostuser.`accountid` AND subacc.`account_type`=1 

    AND sublog.`caller` =#{called} 


   AND subacc.`voip_status`=#{voip_status}


    AND DATE(sublog.`starttime`) >=#{starttime} 


    AND DATE(sublog.`endtime`) <=#{endtime}

 ORDER BY subacc.`account_id` LIMIT #{startLimit},#{pageSize}

SELECT  count(sublog.`recordurl`) FROM   `t_itov_ptt_subaccount_log` sublog,`t_itov_ptt_subaccount` subacc ,`t_itov_account` account,`t_zdc_host_user` hostuser WHERE sublog.`called`=subacc.`voipAccount`
 AND subacc.`account_id` = account.`account_id`AND subacc.`account_id` = hostuser.`accountid` AND subacc.`account_type`=1

    AND sublog.`called` =#{called} 


   AND subacc.`voip_status`=#{voip_status}


    AND DATE(sublog.`starttime`) >=#{starttime} 


    AND DATE(sublog.`endtime`)<=#{endtime}

SELECT DISTINCT(subacc.`voipAccount`),account.`account_name`,subacc.`account_id`,subacc.`voip_status`,hostuser.`name`,hostuser.`telphone`,hostuser.`sex`,hostuser.`email` FROM  `t_itov_ptt_subaccount` subacc ,`t_itov_account` account,`t_zdc_host_user` hostuser WHERE 
subacc.`account_id` = account.`account_id`AND subacc.`account_id` = hostuser.`accountid` AND subacc.`account_type`=1 

     AND subacc.`voip_status`=#{voip_status}

ORDER BY subacc.`account_id` LIMIT #{startLimit},#{pageSize}

SELECT COUNT(DISTINCT subacc.`voipAccount`) FROM `t_itov_ptt_subaccount` subacc ,`t_itov_account` account,`t_zdc_host_user` hostuser WHERE subacc.`account_id` = account.`account_id`AND subacc.`account_id` = hostuser.`accountid` AND subacc.`account_type`=1
AND subacc.`voip_status`=#{voip_status}
ORDER BY subacc.`account_id`

SELECT a.`account_name`,a.`account_password`,sub.`voip_status`,sub.`voipPwd`,sub.`voipAccount`,sub.`hostType`,sub.`channelType`,sub.`roomId`,hos.* FROM `t_itov_account` a INNER JOIN `t_itov_ptt_subaccount` sub ON a.`account_id`=sub.`account_id` 
INNER JOIN `t_zdc_host_user` hos ON hos.`accountid`=a.`account_id`
AND a.`account_name`=#{account_name} AND a.`account_password`=#{account_password}  AND sub.`hostType`=#{hostType}
