mapper.delete:
delete from t_itov_weathercitycode
 where city_code = #{cityCode,jdbcType=VARCHAR}
   and city_name = #{cityName,jdbcType=VARCHAR}

mapper.select:
select 

  from t_itov_weathercitycode
 where city_code = #{cityCode,jdbcType=VARCHAR}
   and city_name = #{cityName,jdbcType=VARCHAR}

select 
  from t_itov_weathercitycode where 1=1    

   and city_code = #{cityCode,jdbcType=VARCHAR}


   and  city_name = #{cityName,jdbcType=VARCHAR}
